sidebarCSS:
/* ROOT */

#root {
	/* Tab and PinnedTab height adjustment */
	--tabs-height: 30px;
	--tabs-pinned-height: var(--tabs-height);
	/* Dark Theme base settings */
	--bg: #11111B !important;
	--tabs-fg: #d4d4d4 !important;
	--tabs-bg-hover: #3b3948 !important;
	--tabs-activated-bg: #313140 !important;
	--nav-btn-activated-bg: #1f222f !important;
}

#root .Tab {
	--tabs-indent: 8px;
}

/* popup-container */
.popup-container {
  padding: 6px;
}

/* TABS */
.Tab .lvl-wrapper:after {
	display: none;
}
.Tab {
	margin: auto;
	width: calc(100% - 8px);
	border-radius: 3px;
}
.Tab .title {
	overflow: hidden !important;
  font-size: 0.98em;
}

/* SubPanel */
.SubPanel {
  min-width: 240px;
}

/* PINNED TABS */
.PinnedTabsBar {
	margin-left: 2px;
  min-height: 32px;
  flex-wrap: nowrap !important;
  overflow-x: scroll;
  overflow-y: none;
  scrollbar-width: none;
}

/* NAV BAR */
.NavigationBar {
	min-height: 38px !important;
	padding: 4px 0px 4px 4px !important;
	background-color: # !important;
	margin-bottom: 4px;
	box-shadow: none !important;
}
.NavigationBar .panel-btn {
  border-radius: 3px !important;
}
.NavigationBar .panel-btn:hover {
	background-color: var(--tabs-bg-hover) !important;
}
.NavigationBar .panel-btn[data-active="true"] {
	background-color: var(--bg) !important;
}
.NavigationBar .panel-btn[data-active="true"]:hover {
	background-color: var(--tabs-bg-hover) !important;
}

/* SIDEBAR HACK PATCHES */
@media (max-width: 40px) {
	/* Fix to tab tree identation when minimised */
	#root:not(:hover) .Tab {
		--tabs-indent: 0px;
	}

	.Tab[data-lvl] > div.lvl-wrapper {
		right: -2px;
	}

	.Tab {
		max-width: 34px;
	}

	.Tab .fav {
		z-index: 1;
	}

	.Tab .audio {
		z-index: 1;
		margin-left: -12px;
		height: 26px;
	}

	.Tab .title {
		display: none;
	}
  .BottomBar {
    display: none;
  }
  .new-tab-btns {
    display: none !important;
  }
  .Tab .audio {
    display: none;
  }
}
.popup-container, .main-items>*, .new-tab-btns, .BottomBar {
  animation: appearing-animation var(--d-norm);
}
@media (max-width: 238px) {
	.main-items>*:not([data-active="true"]) {
    display: none;
  }
  .PinnedTabsBar {
    position: relative !important;
  }
  .PinnedTabsBar .tab-wrapper {
    position: absolute !important;
  }
  .PinnedTabsBar .Tab[data-active="false"] {
    background: #11111B;
  }
	.PinnedTabsBar>.tab-wrapper:first-child>.Tab {
    z-index: 1000;
  }
  .PinnedTabsBar .Tab[data-active="true"] {
    z-index: 1001;
  }
  .popup-container {
    display: none;
  }
}
@media (min-width: 238px) {
  .PinnedTabsBar .Tab[data-active="false"] {
    animation: appearing-animation var(--d-norm);
  }
}


