/* OLD STYLES
/* ROOT */
@media (prefers-color-scheme: dark) {
    #root {
        /* Dark Theme base settings */
        --bg: #1E1E2E !important;
        --tabs-fg: #d4d4d4 !important;
        --tabs-bg-hover: #3b3948 !important;
        --tabs-activated-bg: #5b5b66 !important;
        --nav-btn-activated-bg: #1f222f !important;
        --border-fg: rgba(0,0,0,0.196) !important;
    }
    /* Shadows */
    .top-shadow {
        box-shadow: rgba(0, 0, 0, 0.392) 0px -1px 12px 0px, rgba(0, 0, 0, 0.07) 0px -1px 0px 0px !important;
    }
    .bottom-shadow {
        box-shadow: rgba(0, 0, 0, 0.392) 0px -1px 12px 0px, rgba(0, 0, 0, 0.07) 0px -1px 0px 0px !important;
    }
    /* Navbar */
    .NavigationBar {
        background-color: #2b2a33 !important;
    }
    .NavigationBar .panel-btn > svg,
    .NavigationBar .panel-btn > img {
        fill: #f2f2f2 !important;
    }
}
@media (prefers-color-scheme: light) {
    #root {
        /* Dark Theme base settings */
        --bg: #E6E9EF !important;
        --tabs-fg: #1E1E2E !important;
        --tabs-bg-hover: #d6d9e0 !important;
        --tabs-activated-bg: #cccfd6 !important;
        --nav-btn-activated-bg: #cbcfd6 !important;
        --border-fg: rgba(0,0,0,0.9) !important;
    }
    /* Shadows */
    .top-shadow {
        box-shadow: rgba(0, 0, 0, 0.18) 0px -1px 12px 0px, rgba(0, 0, 0, 0.002) 0px -1px 0px 0px !important;
    }
    .bottom-shadow {
        box-shadow: rgba(0, 0, 0, 0.18) 0px -1px 12px 0px, rgba(0, 0, 0, 0.002) 0px -1px 0px 0px !important;
    }
    /* Navbar */
    .NavigationBar {
        background-color: transparent !important;
        box-shadow: none !important;
    }
    .NavigationBar .panel-btn > img {
        fill: #1a1a1a !important;
    }
}
